import React from 'react';
import { useNavigate } from 'react-router-dom';

const Footer = () => {
  const navigate = useNavigate();

  const handleNavigation = (hash) => {
    // Przejście do strony głównej + hash
    navigate(`/#${hash}`);

    // Ręczny scroll (upewnij się, że sekcja istnieje)
    setTimeout(() => {
      const element = document.getElementById(hash);
      if (element) {
        element.scrollIntoView({ behavior: 'smooth' });
      } else {
        window.scrollTo(0, 0);
      }
    }, 100);
  };

  return (
    <section className="flex flex-row items-center w-full justify-center mq450:h-auto">
    <div id="container" className="flex flex-col max-w-[1280px] w-full justify-between px-[24px] gap-[64px] mq450:px-4 h-full py-[80px]">
    <div id="top" className="flex flex-row gap-[64px] justify-center items-end">
        <div id="left" className="flex flex-col w-full gap-[16px] items-center justify-center">
            <div id="title" className='font-satoshi-bold font-[600] text-[18px] leading-[130%] text-[#4040F2]'>PLANY</div>
            <div id="subtitle" className='font-satoshi-bold font-[600] text-[40px] leading-[130%] text-[#4040F2]'>Wybierz Plan Idealny dla Ciebie</div>
        </div>
    </div>

    </div>
  </section>
  );
};

export default Footer;
