import React from 'react';
import { useNavigate } from 'react-router-dom';

const Footer = () => {
  const navigate = useNavigate();

  const handleNavigation = (hash) => {
    // Przejście do strony głównej + hash
    navigate(`/#${hash}`);

    // Ręczny scroll (upewnij się, że sekcja istnieje)
    setTimeout(() => {
      const element = document.getElementById(hash);
      if (element) {
        element.scrollIntoView({ behavior: 'smooth' });
      } else {
        window.scrollTo(0, 0);
      }
    }, 100);
  };

  return (
    <section className="flex flex-row items-center w-full justify-center mq450:h-auto">
    <div id="container" className="flex flex-col max-w-[1280px] w-full justify-between px-[24px] gap-[64px] mq450:px-4 h-full py-[80px]">
    <div id='left'>

    </div>
    <div id='right'>

    </div>
    </div>
  </section>
  );
};

export default Footer;
