import React, { useState, useEffect } from 'react';
import BurgerIcon from './Burger';
import classnames from 'classnames';

const Hero = () => {
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 450);
    };

    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <section
      className="flex flex-row items-center w-full justify-center py-12 mq450:py-8"
      id="home"
    >
      <div id="container" className="flex flex-col max-w-[1280px] w-full justify-between px-[24px] mq450:px-4">
        <div className='flex w-full flex-col md:flex-row gap-8'>
          <div className='flex flex-col w-full' id='left'>
            <h1 className='text-3xl font-bold mb-4'>Teraz.cash - Twoje finanse, Twoje zasady!</h1>
          </div>
          <div className='flex w-full justify-center items-center' id='right'>
            <img
              src='/hero-img.svg'
              alt='Hero'
              className='max-w-full h-auto'
            />
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
