import React, { useRef, useState, useEffect } from 'react';
import BurgerIcon from './Burger';
import classnames from 'classnames';

const Hero = () => {
  const [isMenuVisible, setIsMenuVisible] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const setHeight = () => {
      document.documentElement.style.setProperty('--vh', `${window.innerHeight * 0.01}px`);
      setIsMobile(window.innerWidth <= 450);
    };

    setHeight();
    window.addEventListener('resize', setHeight);
    return () => window.removeEventListener('resize', setHeight);
  }, []);

  const toggleMenu = () => {
    setIsMenuVisible((prev) => {
      document.body.style.overflow = prev ? 'auto' : 'hidden';
      return !prev;
    });
  };

  return (
    <section
      className="bg-[#3B5140] flex flex-row items-center w-full justify-center mq450:h-auto"
      style={!isMobile ? { height: 'calc(var(--vh, 1vh) * 100)' } : { height: 'auto' }}
      id="home"
    >
      <div id="container" className="flex flex-col max-w-[1440px] w-full justify-between px-10 mq450:px-4 h-full">
        <div className='flex w-full h-full pt-[96px] pb-[32px] mq450:pt-[72px]'>
          <div id="border-first" className="flex w-full h-auto p-4" style={{ boxShadow: 'inset 0 0 0 3px #BF6EA3' }}> 
            <div id="border-second" className="flex flex-row mq450:flex-col w-full h-auto p-[48px] mq450:p-[24px] gap-[48px]" style={{ boxShadow: 'inset 0 0 0 3px #BF6EA3' }}>
              <div id='left' className='flex flex-col w-full h-auto justify-center gap-[48px] mq450:gap-[32px] mq450:order-2'>
                <div id='top' className='flex flex-col gap-[32px] mq450:gap-[24px]'>
                  <div id='title' className='text-[96px] font-campaign-serif leading-[90%] text-[#ECED94] mq450:text-[40px]'>Your brand. <br/> Our jellies.</div>
                  <div id='subtitle' className='text-[44px] font-campaign-serif leading-[120%] text-[#ECED94] mq450:text-[24px]'>Innovative supplements in a form your customers will love.</div>
                  <div id='text' className='text-[18px] text-[#ECED94] font-sofia-light tracking-[0.05em] leading-[140%] mq450:text-[16px]'>
                    We create functional jellies for brands across Europe. White label from concept to delivery â€“ quickly, comprehensively and with guaranteed quality.
                  </div>
                </div>
                <div id='bts' className='flex flex-row gap-[20px] mq450:flex-col'>
                  <div id='btn' onClick={() => window.open('https://jellyfairy2.pipedrive.com/scheduler/drqdlPI5/jelly-fairy-intro-meeting', '_blank')} className='flex cursor-pointer px-[24px] items-center justify-center h-[56px] text-[16px] text-white bg-[#BF6EA3] font-sofia-light tracking-[0.05em]'>
                    Schedule a meeting
                  </div>
                  <div id='btn' onClick={() => window.location.href = 'tel:+48574208420'} className='flex px-[24px] cursor-pointer items-center justify-center h-[56px] text-[16px] text-[#3B5140] bg-[#ECED94] font-sofia-light tracking-[0.05em]'>
                    Call now +48 574 208 420
                  </div>
                </div>
              </div>

              <div id='right' className='flex flex-col w-full h-auto justify-center mq450:order-1'>
                <img src='/hero-img.svg' className='w-full' alt="Hero" />
              </div>

            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
