import React from 'react';
import { useNavigate } from 'react-router-dom';

const Footer = () => {
  const navigate = useNavigate();

  const handleNavigation = (hash) => {
    // Przejście do strony głównej + hash
    navigate(`/#${hash}`);

    // Ręczny scroll (upewnij się, że sekcja istnieje)
    setTimeout(() => {
      const element = document.getElementById(hash);
      if (element) {
        element.scrollIntoView({ behavior: 'smooth' });
      } else {
        window.scrollTo(0, 0);
      }
    }, 100);
  };

  return (
    <section className="flex flex-row items-center w-full justify-center mq450:h-auto">
    <div id="container" className="flex flex-col max-w-[1280px] w-full justify-between px-[24px] gap-[64px] mq450:px-4 h-full py-[80px]">
    <div id='top' className='flex w-full flex-row justify-between'>
    <div id='left' className='flex flex-col gap-[24px] w-full'>
    <img src='footer-logo.svg' className='h-[32px]' />
    <div id='title' className='font-satoshi-medium font-[400] text-[24px] leading-[150%] text-[#4040F2]'>Twoje finanse, Twoje zasady!</div>
    </div>
    <div id='right'>
    <div id='title' className='font-satoshi-medium font-[400] text-[24px] leading-[150%] text-[#4040F2]'>Twoje finanse, Twoje zasady!</div>
    <div id='title' className='font-satoshi-medium font-[400] text-[24px] leading-[150%] text-[#4040F2]'>Twoje finanse, Twoje zasady!</div>
    </div>
    </div>
    </div>
  </section>
  );
};

export default Footer;
