import React, { useState, useEffect } from 'react';

const Hero = () => {
  const [isMenuVisible, setIsMenuVisible] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const setHeight = () => {
      document.documentElement.style.setProperty('--vh', `${window.innerHeight * 0.01}px`);
      setIsMobile(window.innerWidth <= 450);
    };

    setHeight();
    window.addEventListener('resize', setHeight);
    return () => window.removeEventListener('resize', setHeight);
  }, []);

  const toggleMenu = () => {
    setIsMenuVisible((prev) => {
      document.body.style.overflow = prev ? 'auto' : 'hidden';
      return !prev;
    });
  };

  return (
    <section
      className="flex flex-row items-center w-full justify-center mq450:h-auto"
      style={{ height: 'auto' }}
      id="home"
    >
      <div id="container" className="flex flex-col max-w-[1280px] w-full justify-between px-[24px] mq450:px-4 h-full">
        <div className='flex w-full h-full pt-[96px] pb-[32px] mq450:pt-[72px]'>
        <div id='content' className='flex flex-row w-full py-[64px]'>
        <div className='flex flex-col w-full gap-[40px]' id='left'>
        <div id='title' className='font-satoshi-bold text-[64px]'></div>Teraz.cash -Twoje finanse, Twoje zasady!
        </div>
        <div className='flex w-full justify-center items-center' id='right'>
          <img
            src='/hero-img.svg'
          />
        </div>
        </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
